package ex02variable;

public class Qu_02_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int kor, eng, math;
		kor = 99;
		eng = 80;
		math = 96;
		System.out.println("평균점수(실수형)=" + (kor + eng + math) / 3.0);
		System.out.println("평균점수(정수형)=" + (kor + eng + math) / 3);
	}

}
